import { Component,OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ActivatedRoute,Params } from '@angular/router';
@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss']
})
export class UsersComponent {
  // 
  constructor(private route: Router,private ActiveRoute:ActivatedRoute){}

  user!:{id:string; name:string};

   ngOnInit():void{
    // this.user={
    //   id:this.ActiveRoute.snapshot.params['id'],
    //   name:this.ActiveRoute.snapshot.params['name']
    // }
   this.ActiveRoute.params.subscribe((data:Params)=>{
    this.user={
      id:data['id'],
      name:data['name'],
    }
   });
  }

  navToCategoriesbtn(){
    this.route.navigate(['/categories']);
  }
}
<h4>Welcome to Users</h4>

<!-- Navigating a link through button (program logic in User.comp.ts file) -->
<button class="btn btn-warning" (click)="navToCategoriesbtn()">Go to categories</button>
<div>
    <br>
    <p>Passing and fetching parameters in routes</p>
    <div>User id: {{user.id}}</div>
    <div>User id: {{user.name}}</div>
  Child Route
    <a [routerLink]="['/users',1,'kural']" >Get Kural details</a> <br>
    <a [routerLink]="['/users',2,'M']" >Get Person M details</a>

    <div>
        <router-outlet> </router-outlet>
    </div>
